const src = $input.first()?.json?.body ?? $input.first()?.json ?? {};
const delivery = src.delivery ?? {};
const channel = src.channel ?? {};
const payload = delivery.payload ?? {};
const media = Array.isArray(payload.media) ? payload.media : [];

const renderedText =
  delivery.rendered_text ??
  src.rendered_text ??
  payload.text ??
  '';

const deliveryIdRaw = delivery.delivery_id ?? src.delivery_id ?? null;
const deliveryId =
  deliveryIdRaw === null || deliveryIdRaw === undefined
    ? null
    : String(deliveryIdRaw);

const appendDeliveryId = Boolean(
  channel.append_delivery_id ??
    delivery.meta?.append_delivery_id ??
    src.append_delivery_id ??
    false,
);

const renderedTextEffective =
  appendDeliveryId && deliveryId
    ? `${renderedText}\n\n[d:${deliveryId}]`
    : renderedText;

const parseModeRaw = String(
  delivery.meta?.parse_mode ?? src.meta?.parse_mode ?? 'None',
);
let telegramParseMode = null;
if (parseModeRaw === 'HTML' || parseModeRaw === 'Markdown' || parseModeRaw === 'MarkdownV2') {
  telegramParseMode = parseModeRaw;
}

return [{
  json: {
    workspace_id: src.workspace_id ?? null,
    delivery,
    channel,
    delivery_id: deliveryId,
    append_delivery_id: appendDeliveryId,
    target_id: channel.target_id ?? delivery.target_id ?? src.target_id ?? null,
    rendered_text: renderedText,
    rendered_text_effective: renderedTextEffective,
    media,
    telegram_parse_mode: telegramParseMode,
  },
}];

const input = $input.first()?.json ?? {};
return [{
  json: {
    ok: false,
    error: {
      category: 'PERMANENT',
      scope: 'delivery',
      code: 'missing_target_id',
      message: 'channel.target_id is required for telegram adapter',
      raw: input,
    },
  },
}];

const input = $input.first()?.json ?? {};

const sourceMedia = Array.isArray(input.media) ? input.media : [];

const normalizeKind = (item) => {
  const kindSource = String(item?.type ?? item?.kind ?? '').toLowerCase();
  return kindSource.includes('video') || kindSource === 'gif' || kindSource === 'animation'
    ? 'video'
    : 'photo';
};

const normalizedMedia = sourceMedia
  .map((itemRaw, index) => {
    const item = itemRaw && typeof itemRaw === 'object' ? itemRaw : null;
    if (!item) return null;

    return {
      index,
      blob_ref: item.blob_ref ?? null,
      type: item.type ?? null,
      kind: normalizeKind(item),
      origin_url: item.origin_url ?? null,
      url: item.url ?? null,
      media_url: item.media_url ?? null,
      file_id: item.file_id ?? null,
      telegram_file: item.file_id ?? item.telegram_file ?? null,
    };
  })
  .filter(Boolean);

const primary = normalizedMedia.length > 0 ? normalizedMedia[0] : null;

return [{
  json: {
    ...input,
    normalized_media: normalizedMedia,
    primary_media: primary,
    has_multiple_media: normalizedMedia.length > 1,
  },
}];

const base = $items('Select Primary Media')[0]?.json ?? {};
const cache = $input.first()?.json ?? {};

const resolvedPrimaryFile =
  cache.file_id ??
  base.primary_media?.telegram_file ??
  cache.origin_url ??
  base.primary_media?.origin_url ??
  base.primary_media?.url ??
  base.primary_media?.media_url ??
  null;

const primary = base.primary_media
  ? {
      ...base.primary_media,
      telegram_file: resolvedPrimaryFile,
    }
  : null;

const normalizedMedia = Array.isArray(base.normalized_media)
  ? base.normalized_media.map((item, index) => {
      if (index !== 0 || !item || typeof item !== 'object') return item;
      return {
        ...item,
        telegram_file: resolvedPrimaryFile,
      };
    })
  : [];

return [{
  json: {
    ...base,
    primary_media: primary,
    normalized_media: normalizedMedia,
  },
}];

const base = $input.first()?.json ?? {};

const resolvedPrimaryFile =
  base.primary_media?.telegram_file ??
  base.primary_media?.origin_url ??
  base.primary_media?.url ??
  base.primary_media?.media_url ??
  null;

const primary = base.primary_media
  ? {
      ...base.primary_media,
      telegram_file: resolvedPrimaryFile,
    }
  : null;

const normalizedMedia = Array.isArray(base.normalized_media)
  ? base.normalized_media.map((item, index) => {
      if (index !== 0 || !item || typeof item !== 'object') return item;
      return {
        ...item,
        telegram_file: resolvedPrimaryFile,
      };
    })
  : [];

return [{
  json: {
    ...base,
    primary_media: primary,
    normalized_media: normalizedMedia,
  },
}];

const raw = $input.first()?.json ?? {};

const classifyError = (err) => {
  const message = String(
    typeof err === 'string'
      ? err
      : err?.message ?? err?.description ?? err?.error ?? 'telegram_send_failed',
  );
  const code = Number(
    typeof err === 'object' && err
      ? err.httpCode ?? err.statusCode ?? err.code ?? 0
      : 0,
  );

  let category = 'PERMANENT';
  let scope = 'delivery';
  let normalizedCode = 'telegram_bad_request';

  const channelUnavailablePattern =
    /chat not found|bot was kicked|bot was blocked by the user|user is deactivated|group chat was deleted|chat_write_forbidden|have no rights to send a message|need administrator rights|bot can't initiate conversation/i;

  if (code === 429 || /retry after/i.test(message)) {
    category = 'TRANSIENT';
    scope = 'platform';
    normalizedCode = 'rate_limited';
  } else if (
    code >= 500 ||
    /timed out|timeout|network|socket|econn|etimedout|fetch failed/i.test(message)
  ) {
    category = 'TRANSIENT';
    scope = 'platform';
    normalizedCode = 'upstream_unavailable';
  } else if (/message is too long|text is too long|caption is too long/i.test(message)) {
    category = 'PERMANENT';
    scope = 'delivery';
    normalizedCode = 'message_too_long';
  } else if (
    code === 401 ||
    (code === 403 && channelUnavailablePattern.test(message))
  ) {
    category = 'PERMANENT';
    scope = 'channel';
    normalizedCode = 'channel_unavailable';
  }

  const retryMatch = message.match(/retry after\s*(\d+)/i);
  const retryAfterMs = retryMatch ? Number(retryMatch[1]) * 1000 : null;

  return {
    category,
    scope,
    code: normalizedCode,
    retry_after_ms: Number.isFinite(retryAfterMs) ? retryAfterMs : undefined,
    message,
    raw: err,
  };
};

const itemsOrEmpty = (nodeName) => {
  try {
    return $items(nodeName);
  } catch {
    return [];
  }
};

const context =
  itemsOrEmpty('Build Media Group Payload')[0]?.json ??
  itemsOrEmpty('Apply Primary Cache')[0]?.json ??
  itemsOrEmpty('Primary Without Cache')[0]?.json ??
  itemsOrEmpty('Select Primary Media')[0]?.json ??
  itemsOrEmpty('Normalize Adapter Input')[0]?.json ??
  {};

if (raw?.ok === false && raw?.error) {
  return [{ json: { result: raw, cache: null } }];
}

if (raw?.error) {
  return [
    {
      json: {
        result: {
          ok: false,
          error: classifyError(raw.error),
        },
        cache: null,
      },
    },
  ];
}

const resultEnvelope = raw?.body?.result ?? raw?.result ?? raw;
const resultItems = Array.isArray(resultEnvelope) ? resultEnvelope : [resultEnvelope];
const firstResult =
  resultItems.find((item) => item && typeof item === 'object') ?? {};

const providerMessageId =
  firstResult?.message_id ??
  firstResult?.id ??
  raw?.message_id ??
  `tg-${Date.now()}`;

const pickPhotoFileId = (value) => {
  const photoArray = value?.photo ?? value?.message?.photo ?? [];
  if (Array.isArray(photoArray) && photoArray.length > 0) {
    return photoArray[photoArray.length - 1]?.file_id ?? null;
  }
  return null;
};

const pickVideoFileId = (value) =>
  value?.video?.file_id ?? value?.message?.video?.file_id ?? null;

const primary = context?.primary_media ?? null;
const primaryKind = String(primary?.kind ?? '').toLowerCase();

let fileId = null;
if (primaryKind === 'video') {
  fileId = pickVideoFileId(firstResult) ?? pickPhotoFileId(firstResult);
} else {
  fileId = pickPhotoFileId(firstResult) ?? pickVideoFileId(firstResult);
}

const canCache = !!context?.workspace_id && !!primary?.blob_ref && !!fileId;

return [
  {
    json: {
      result: {
        ok: true,
        provider_message_id: String(providerMessageId),
        delivery_id: context.delivery_id ?? null,
        raw,
      },
      cache: canCache
        ? {
            workspace_id: context.workspace_id,
            blob_ref: primary.blob_ref,
            file_id: fileId,
          }
        : null,
    },
  },
];

const input = $input.first()?.json ?? {};
if (input?.result_json) {
  return [{ json: input.result_json }];
}
if (input?.result) {
  return [{ json: input.result }];
}
if (input?.ok === true || input?.ok === false) {
  return [{ json: input }];
}

return [{
  json: {
    ok: false,
    error: {
      category: 'TRANSIENT',
      scope: 'platform',
      code: 'adapter_output_unexpected',
      message: 'adapter_telegram_send returned unexpected payload shape',
      raw: input,
    },
  },
}];

const input = $input.first()?.json ?? {};
const media = Array.isArray(input.normalized_media) ? input.normalized_media : [];
const parseMode = input.telegram_parse_mode ?? null;
const caption = String(input.rendered_text_effective ?? input.rendered_text ?? '');

if (media.length < 2) {
  return [{ json: { ...input, telegram_media_group_payload: { media: [] } } }];
}

const groupItems = [];

for (let index = 0; index < media.length; index += 1) {
  const item = media[index] ?? {};
  if (!item || typeof item !== 'object') continue;

  const mediaValue =
    item.file_id ??
    item.telegram_file ??
    item.origin_url ??
    item.url ??
    item.media_url ??
    null;

  if (!mediaValue) {
    return [
      {
        json: {
          ok: false,
          error: {
            category: 'PERMANENT',
            scope: 'delivery',
            code: 'media_missing_origin',
            message: `media[${index}] missing origin_url/url/media_url or cached file_id`,
            raw: { index, item },
          },
        },
      },
    ];
  }

  const mediaType = String(item.kind ?? item.type ?? '').toLowerCase().includes('video')
    ? 'video'
    : 'photo';

  const entry = {
    type: mediaType,
    media: String(mediaValue),
  };

  const additionalFields = {};
  if (index === 0 && caption) {
    additionalFields.caption = caption;
  }
  if (index === 0 && parseMode) {
    additionalFields.parse_mode = parseMode;
  }
  if (Object.keys(additionalFields).length > 0) {
    entry.additionalFields = additionalFields;
  }

  groupItems.push(entry);
}

return [{
  json: {
    ...input,
    telegram_media_group_payload: {
      media: groupItems,
    },
  },
}];
